int writeChar(Display::Display& display, int col, uint32_t color, char c) {
  if (c == ' ') {
    return 3;
  }
  unsigned int charDefOfs = ofs[c - '!'];
  unsigned int charDefSize = ofs[c - '!' + 1] - charDefOfs;
  for (uint8_t row = 0; row < 6; row++) {
    for (unsigned int charCol = 0; charCol < charDefSize; charCol++) {
      int ledCol = charCol + col;
      if (ledCol >= 0 && ledCol < display.width()) {
        if (char_data[row][charCol + charDefOfs]) {
          display.setPixel(row + 1, ledCol, color);
        }
      }
    }
  }
  return charDefSize;
}

int writeString(Display::Display& display, int col, uint32_t color, const String& msg) {
  int charStart = col;
  for (const char* c = msg.begin(); c != msg.end(); c++) {
    int charSize = writeChar(display, charStart, color, *c);
    charStart += charSize;
  }
  return charStart;
}

Ticker::Ticker(Display::Display& display): DisplayRoutine(display) {
  col = display.width() - 1;
}

void Ticker::init() {

}

void Ticker::step(const DisplayConfigServer::DisplayConfig& config) {
    if (msgStr != config.message) {
        msgStr = config.message;
        col = display.width() - 1;
        display.clear();
    }
    writeString(display, col, config.bkgColor, msgStr);
    int minColVal = -((int)msgStr.length() * MAX_CHAR_WIDTH);
    col = (col < minColVal) ? display.width() - 1 : col - 1;
    writeString(display, col, config.textColor, msgStr);
}

